import React, { useState } from "react";

import { useForm } from 'react-hook-form';

import '../css/style.css';

import minhaImagem from '../images/login.jps';

import verified from '../images/verified.png';


function MyForm() {
    const { register, handleSubtmit, formState: {errors } } = useForm();

    const [status, setStatus] = useState({ submittedSucessfully: false, loading: false, showPassword: false});

    const onSubmit = data => {
        setStatus({ ...status, loading: true });

        setTimeout(() =>{
            setStatus({ submittedSucessfully: true,loading: false});
        }, 1000);
    };
    const generateErrorMessage = fieldName => {
        return {
            required: `${fieldName} é obrigatório`,
            pattern: fieldName === 'email' ? 'Formato de e-mail inválido' : null
        };
    };
    const handleReturnButtonClick = () => {
        window.location.href = '/';
    };
    return ( 
        <div className='container'>
            {status.submittedSucessfully ? (
                <div className='sucess-message'>
                    <img src={verified} alt="Verificado" />
                    <h2>Formulário Enviado com Sucesso!</h2>
                    <p>Obrigado por enviar o formulário.</p>
                    <button className="btn" onClick={handleReturnButtonClick}>Retornar</button>
                </div>
            ) : (
                <div className='form-sign-up'>
                    <div>
                        <img src={minhaImagem} alt="imagem de uma pessoa se cadastrando pelo celular" />
                    </div>

                    <section>
                        <h1>Inscreva-se</h1>

                        {/* Formulário com campos controlados pelo hook useForm*/}
                        <form onSubmit={handleSubtmit(onSubmit)}>
                            <div className='form flex'>
                                <label htmlFor='name'>Nome</label>
                                <input 
                                    {...register("firstName", generateErrorMessage("Nome"))}
                                    placeholder="Nome"
                                    id='name'
                                    autoComplete='off'
                                    className={errors.firstName ? 'error' : ''}
                                />

                                {errors.firstName && <span className='error-message'>{errors.firstName.message}</span>}
                            </div>

                            <div className='form flx'>

                            </div>
                        </form>
                    </section>
                </div>
            )
            }
        </div>
    )

}
